    import org.slf4j.Logger;
    import org.slf4j.LoggerFactory;
		class DateFormatTest {
		  protected final Logger logger = LoggerFactory.getLogger(getClass());
      protected final Logger ttt = LoggerFactory.getLogger(getClass());
      public void fun () {
        String a;
        String b;
        logger.info();
        logger.debug("1"+"2");
        logger.trace(a+b);
        ttt.info(a+"1");
        ttt.trace(1+a+b);
        ttt.error(a+b);
        logger.warn(a+b);
      }
    }
